import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { Form, Button } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import Loader from '../Components/Loader'
import Message from '../Components/Message'
import FormContainer from '../Components/FormContainer'
import { listPackageDetails, updatePackage } from '../actions/packageActions'
import { PACKAGE_UPDATE_RESET } from '../constants/packageConstants'
import axios from 'axios'

function PackageEditScreen({ match, history }) {
    
    const packageId = match.params.id

    const [title, setTitle] = useState('')
    const [price, setPrice] = useState(0)
    const [description, setDescription] = useState('')
    
    const dispatch = useDispatch()

    const packageDetails = useSelector(state => state.packageDetails)
    const { error, loading, packages } = packageDetails

    const packageUpdate = useSelector(state => state.packageUpdate)
    const { error: errorUpdate, loading: loadingUpdate, success: successUpdate } = packageUpdate

    useEffect(() => {

        if (successUpdate) {
            dispatch({ type: PACKAGE_UPDATE_RESET })
            history.push('/subscription')
        } else {
            if (!packages.name || packages.id !== Number(packageId)) {
                dispatch(listPackageDetails(packagesId))
            } else {
                setTitle(packages.title)
                setPrice(packages.price)
                setDescription(packages.description)

            }
        }
    }, [dispatch, packages, packageId, history, successUpdate])


    const submitHandler = (e) => {
        e.preventDefault()
        dispatch(updatePackage({
            id: packageId,
            price,
            description
        }))
    }

    return (
        <div>
            <Link to='/subscription'>
                Go Back
            </Link>

            <FormContainer>
                <h1>Edit Package</h1>
                {loadingUpdate && <Loader />}
                {errorUpdate && <Message variant='danger'>{errorUpdate}</Message>}

                {loading ? <Loader /> : error ? <Message variant='danger'>{error}</Message>
                    : (
                        <Form onSubmit={submitHandler}>

                            <Form.Group controlId='title'>
                                <Form.Label>Title</Form.Label>
                                <Form.Control

                                    type='text'
                                    placeholder='Enter title'
                                    value={title}
                                    onChange={(e) => setTitle(e.target.value)}
                                >
                                </Form.Control>
                            </Form.Group>

                            <Form.Group controlId='price'>
                                <Form.Label>Price</Form.Label>
                                <Form.Control

                                    type='number'
                                    placeholder='Enter price'
                                    value={price}
                                    onChange={(e) => setPrice(e.target.value)}
                                >
                                </Form.Control>
                            </Form.Group>

                            <Form.Group controlId='description'>
                                <Form.Label>Description</Form.Label>
                                <Form.Control

                                    type='text'
                                    placeholder='Enter description'
                                    value={description}
                                    onChange={(e) => setDescription(e.target.value)}
                                >
                                </Form.Control>
                            </Form.Group>


                            <Button type='submit' variant='primary'>
                                Update
                        </Button>

                        </Form>
                    )}

            </FormContainer >
        </div>
    )
}

export default PackageEditScreen
